import React from "react";
import Header from "./components/Header";
import Users from "./components/Users";
import AddUser from "./components/AddUser";

class App extends React.Component {
	constructor(props) {
		super(props)
		this.state = {
			users: [
				{
					id: 1,
					firstname: 'Bob',
					lastname: 'Marley',
					bio: 'Lorem	iasd dasdw wq weeew du ewawe rteq weq eeqwo',
					age: 40,
					isHappy: true,
				},
				{
					id: 2,
					firstname: 'John',
					lastname: 'Doe',
					bio: 'Lorem	iasd dasdw wq weeew du ewawe rteq weq eeqwo',
					age: 22,
					isHappy: false,
				},
			]
		}
	}

	render() {
		return (<div>
			<Header title="Список пользователей" />
			<main>
				<Users users={this.state.users} />
			</main>
			<aside>
				<AddUser onAdd={this.addUser} />
			</aside>
		</div>)
	}

	addUser(user) {
		console.log(user)
	}

}

export default App
